package hellojava;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int itr1,itr2;
		System.out.println("5 star pattern program");
		for(itr1=0;itr1<5;itr1++)
		{
			for(itr2=0;itr2<=itr1;itr2++)
				System.out.print("* ");
			System.out.println();

		}

	}

}
